def bipartite(edgelist):

    y = [[x,edgelist.count(x)] for x in set(edgelist)]

    x =  []
    for i in y:
        if i[0][1] == '0':
            i[1]= 0
        x.append([i[0][0],i[0][1],i[1]])
    return x

